 function sendemail($body,$arg1,$arg2,$arg3,$arg4,$arg5,$arg6,$arg7,$arg8,$arg9,$arg10) {
    $mail = new PHPMailer;
    $mail->isSMTP(); // Set mailer to use SMTP
    $mail->Host = '203.199.178.220'; // Specify main and backup SMTP servers // 220
    $mail->SMTPAuth = true; // Enable SMTP authentication
    $mail->Username = 'alerts'; // SMTP username
    $mail->Password = 'alert@123'; // SMTP password // TCP port to connect to
    $mail->From = 'alerts@imimobile.com';
    $mail->addAddress('david.wintour@imimobile.com', 'Foobar'); // Add a recipient
    $mail->addCC('Shirwan.khan@imimobile.com'); // Add a recipient
    $mail->addCC('Alex.Sparey@imimobile.com'); // Add a recipient
    $mail->addAttachment($arg1); // Add attachments if passed in
    $mail->addAttachment($arg2); // Add attachments if passed in
    $mail->addAttachment($arg3); // Add attachments if passed in
    $mail->addAttachment($arg4); // Add attachments if passed in
    $mail->addAttachment($arg5); // Add attachments if passed in
    $mail->addAttachment($arg6); // Add attachments if passed in
    $mail->addAttachment($arg7); // Add attachments if passed in
    $mail->addAttachment($arg8); // Add attachments if passed in
    $mail->addAttachment($arg9); // Add attachments if passed in
    $mail->addAttachment($arg10); // Add attachments if passed in
    $mail->isHTML(true); // Set email format to HTML
    $mail->Subject = 'PFI NIGHTWATCH';
    $mail->Body = $body;
    $mail->AltBody = 'This is the body in plain text for non-HTML mail clients';
    if(!$mail->send()) {
        echo 'Message could not be sent.';
        echo 'Mailer Error: ' . $mail->ErrorInfo;
    } else {
        echo 'Message has been sent';
        echo "<br>";
        echo $body;
        echo "<br>";
    }
	};
	
